"use strict";

module.exports = function(options, imports, register) {
    imports.connect.useSetup(remoteAddress);
    register(null, {
        "connect.remote-address": {}
    });
};

function remoteAddress(req, res, next) {
    req.remoteAddress = "127.0.0.1";
    req.proto = "http"
    next();
}
